import {useState} from 'react';
import saveIcon from './assets/save.png';
import delIcon from './assets/delete.png';
import './css/Note.css';

function Modal({open}){
    if (!open) {
        return null;
    }

    const characterLimit = 400;
    const [content, setContent] = useState('');

    const handleCreate = (event) => {
        if (content.length < characterLimit){
            setContent(event.target.value);
        }     
    };

    const handleCreateClick = () => {
        if (content.length > 0){
            setShown(current => !current);
            handleAddNote(content);
            setContent(''); 
        }
    }

    return (
        <div className='overlay'>
            <div className='note-body'>
                <input type="text" placeholder="Title" maxlength="50"/>
                <textarea type="text" placeholder="Content" value={content}onChange={handleCreate}/>
                <div className='note-footer'>
                    <input type="image" src={saveIcon} alt="Submit" align="right"/>
                    <input type="image" src={delIcon} alt="Submit" align="right"/>
                </div>
            </div>
        </div>
    );
}

export default Modal;
