import './css/Note.css';
import addIcon from './assets/add.png';
import saveIcon from './assets/save.png';
import {useState} from 'react';

function NewNote( {handleAddNote} ) {
    const characterLimit = 400;
    const [content, setContent] = useState('');
    const [isShown, setIsShown] = useState(false);

    const handleCreate = (event) => {
        if (content.length < 200){
            setContent(event.target.value);
        }     
    };

    const handleCreateClick = () => {
        if (content.length > 0){
            setIsShown(current => !current);
            handleAddNote(content);
            setContent(''); 
        }
    };

    const handleNewClick = () => {
        setIsShown(current => !current);
    };

    return(           
        <div className="note-body">
            { !isShown && (
                <div>
                    <input type="image" src={addIcon} alt="Submit" onClick={handleNewClick} align="right"></input> 
                </div>
            )}
            { isShown && (
                <div>
                    <textarea 
                        placeholder='Type something...'
                        value={content}
                        onChange={handleCreate}>
                    </textarea>
                    <div className='note-footer'>
                        {characterLimit - content.length} Remaining
                        <button onClick={handleCreateClick}> Save </button>
                    </div> 
                </div>
            )}
        </div>
    )
}

export default NewNote;
