import React from 'react';
import './css/Note.css';

function NoteSearch({handleSearch}){


    return (
        <div>           
            <input onKeyPress={(e) => {
                        if (e.key === "Enter") {
                            this.setState({ message: e.target.value },
                            () => {
                                alert(this.state.message);
                            });
                        }
                    }}
            type='text' placeholder="Search..."/>
        </div>
    );
}

export default NoteSearch;
